INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '&nbsp;&nbsp;Gimmie That Old Time Religion&nbsp;We will follow Zarathustra,&nbsp;&nbsp;We will worship like the Druids,<br>Zarathustra like we use to,&nbsp;&nbsp;Dancing naked in the woods,<br>I''m a Zarathustra booster,&nbsp;&nbsp;Drinking strange fermented fluids,<br>And he''s good enough for me!&nbsp;&nbsp;And it''s good enough for me!<br>(chorus)&nbsp;&nbsp;&nbsp;&nbsp;(chorus)<br>&nbsp;In the church of Aphrodite,<br>The priestess wears a see-through nightie,<br>She''s a mighty righteous sightie,<br>And she''s good enough for me!<br>(chorus)<br>&nbsp;CHORUS:&nbsp;Give me that old time religion,<br>Give me that old time religion,<br>Give me that old time religion,<br>''Cause it''s good enough for me!');
