INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Santa Claus wears a Red Suit,<br>He must be a communist.<br>And a beard and long hair,<br>Must be a pacifist.<br><br>What''s in that pipe that he''s smoking?<br>&nbsp;-- Arlo Guthrie');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '"You are old," said the youth, "as I mentioned before,<br>And have grown most uncommonly fat:<br>Yet you turned a back-somersault in at the door --<br>Pray what is the reason of that?"&nbsp;&nbsp;"In my youth," said the sage, as he shook his grey locks,<br>"I kept all my limbs very supple&nbsp;By the use of this ointment -- one shilling the box --<br>Allow me to sell you a couple?"<br>&nbsp;-- Lewis Carrol');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Her locks an ancient lady gave&nbsp;Her loving husband''s life to save:<br>And men -- they honored so the dame --&nbsp;Upon some stars bestowed her name.<br>&nbsp;But to our modern married fair,<br>Who''d give their lords to save their hair,<br>No stellar recognition''s given.<br>There are not stars enough in heaven.');
