INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'From too much love of living,<br>From hope and fear set free,<br>We thank with brief thanksgiving,<br>Whatever gods may be,<br>That no life lives forever,<br>That dead men rise up never,<br>That even the weariest river winds somewhere safe to sea.<br>&nbsp;-- Swinburne');
