INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Madam, there''s no such thing as a tough child -- if you parboil them&nbsp;first for seven hours, they always come out tender.<br>&nbsp;-- W. C. Fields');
