INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'HOW YOU CAN TELL THAT IT''S GOING TO BE A ROTTEN DAY:<br><br>#32: You call your answering service and they''ve never heard of<br>     you.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'A novice was trying to fix a broken Lisp machine by turning the power&nbsp;off and on.  Knight, seeing what the student was doing spoke sternly:<br>"You can not fix a machine by just power-cycling it with no&nbsp;understanding of what is going wrong."  Knight turned the machine off&nbsp;and on.  The machine worked.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Ignisecond, n.:<br>The overlapping moment of time when the hand is locking the car&nbsp;door even as the brain is saying, "my keys are in there!"<br>&nbsp;-- Rich Hall, "Sniglets"');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Too often I find that the volume of paper expands to fill the available&nbsp;briefcases.<br>&nbsp;-- Governor Jerry Brown');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'There once was an old man from Esser,<br>Who''s knowledge grew lesser and lesser.<br>It at last grew so small,<br>He knew nothing at all,<br>And now he''s a College Professor.');
