INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'The Three Laws of Thermodynamics:<br>&nbsp;The First Law:&nbsp;You can''t get anything without working for it.<br>The Second Law:&nbsp;The most you can accomplish by working is to break<br>&nbsp;even.<br>The Third Law:&nbsp;You can only break even at absolute zero.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Here in my heart, I am Helen;<br>I''m Aspasia and Hero, at least.<br>I''m Judith, and Jael, and Madame de Sta"^Hel;<br>I''m Salome, moon of the East.<br>&nbsp;Here in my soul I am Sappho;<br>Lady Hamilton am I, as well.<br>In me R''^Hecamier vies with Kitty O''Shea,<br>With Dido, and Eve, and poor nell.<br>&nbsp;I''m all of the glamorous ladies<br>At whose beckoning history shook.<br>But you are a man, and see only my pan,<br>So I stay at home with a book.<br>&nbsp;-- Dorothy Parker');
