INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '&nbsp;Love''s Drug&nbsp;&nbsp;My love is like an iron wand <br>That conks me on the head,<br>My love is like the valium <br>That I take before my bed,<br>My love is like the pint of scotch <br>That I drink when I be dry:<br>And I shall love thee still, my dear,<br>Until my wife is wise.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'I was born because it was a habit in those days, people didn''t know&nbsp;anything else ... I was not a Child Prodigy, because a Child Prodigy is&nbsp;a child who knows as much when it is a child as it does when it grows&nbsp;up.<br>&nbsp;-- Will Rogers');
