INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'The man who follows the crowd will usually get no further than the&nbsp;crowd.  The man who walks alone is likely to find himself in places no&nbsp;one has ever been.<br>&nbsp;-- Alan Ashley-Pitt');
