INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Pascal Users:<br>To show respect for the 313th anniversary (tomorrow) of the&nbsp;death of Blaise Pascal, your programs will be run at half speed.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'If God had meant for us to be in the Army, we would have been born with&nbsp;green, baggy skin.');
