INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Pope Goestheveezl was the shortest reigning pope in the history of the&nbsp;Church, reigning for two hours and six minutes on 1 April 1866.  The&nbsp;white smoke had hardly faded into the blue of the Vatican skies before&nbsp;it dawned on the assembled multitudes in St. Peter''s Square that his&nbsp;name had hilarious possibilities.  The crowds fell about, helpless with&nbsp;laughter, singing<br>Half a pound of tuppenny rice<br>Half a pound of treacle<br>That''s the way the chimney smokes<br>Pope Goestheveezl&nbsp;The square was finally cleared by armed carabineri with tears of&nbsp;laughter streaming down their faces.  The event set a record for&nbsp;hilarious civic functions, smashing the previous record set when Baron&nbsp;Hans Neizant B"^Hompzidaize was elected Landburgher of K"^Holn in 1653.<br>&nbsp;-- Mike Harding, "The Armchair Anarchist''s Almanac"');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '"I like work ... I can sit and watch it for hours."');
