INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '"I can remember when a good politician had to be 75 percent ability and&nbsp;25 percent actor, but I can well see the day when the reverse could be&nbsp;true."<br>&nbsp;-- Harry Truman');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '&nbsp;INVENTORY&nbsp;Four be the things I am wiser to know:<br>Idleness, sorrow, a friend, and a foe.<br>&nbsp;Four be the things I''d been better without:<br>Love, curiosity, freckles, and doubt.<br>&nbsp;Three be the things I shall never attain:<br>Envy, content, and sufficient champagne.<br>&nbsp;Three be the things I shall have till I die:<br>Laughter and hope and a sock in the eye.');
