INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Making files is easy under the UNIX operating system.  Therefore, users&nbsp;tend to create numerous files using large amounts of file space.  It&nbsp;has been said that the only standard thing about all UNIX systems is&nbsp;the message-of-the-day telling users to clean up their files.<br>&nbsp;-- System V.2 administrator''s guide');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '"If you can count your money, you don''t have a billion dollars."<br>&nbsp;-- J. Paul Getty');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '(1) Alexander the Great was a great general.<br>(2) Great generals are forewarned.<br>(3) Forewarned is forearmed.<br>(4) Four is an even number.<br>(5) Four is certainly an odd number of arms for a man to have.<br>(6) The only number that is both even and odd is infinity.<br>&nbsp;Therefore, Alexander the Great had an infinite number of arms.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'A Galileo could no more be elected president of the United States than&nbsp;he could be elected Pope of Rome.  Both high posts are reserved for men&nbsp;favored by God with an extraordinary genius for swathing the bitter&nbsp;facts of life in bandages of self-illusion.<br>&nbsp;-- H. L. Mencken');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Modern man is the missing link between apes and human beings.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Nothing makes one so vain as being told that one is a sinner.<br>Conscience makes egotists of us all.<br>&nbsp;-- Oscar Wilde');
