INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '"I have come up with a sure-fire concept for a hit television show,<br>which would be called `A Live Celebrity Gets Eaten by a Shark''."<br>&nbsp;-- Dave Barry, "The Wonders of Sharks on TV"');
