INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'My own dear love, he is strong and bold<br>And he cares not what comes after.<br>His words ring sweet as a chime of gold,<br>And his eyes are lit with laughter.<br>He is jubilant as a flag unfurled --<br>Oh, a girl, she''d not forget him.<br>My own dear love, he is all my world --<br>And I wish I''d never met him.<br>&nbsp;-- Dorothy Parker');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'People need good lies.  There are too many bad ones.<br>&nbsp;-- Bokonon, "Cat''s Cradle" by Kurt Vonnegut, Jr.');
