INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '&nbsp;&nbsp;DETERIORATA&nbsp;&nbsp;Go placidly amid the noise and waste,<br>And remember what comfort there may be in owning a piece thereof.<br>Avoid quiet and passive persons, unless you are in need of sleep.<br>Rotate your tires.<br>Speak glowingly of those greater than yourself,<br>And heed well their advice -- even though they be turkeys.<br>Know what to kiss -- and when.<br>Remember that two wrongs never make a right,<br>But that three do.<br>Wherever possible, put people on "HOLD".<br>Be comforted, that in the face of all aridity and disillusionment,<br>And despite the changing fortunes of time,<br>There is always a big future in computer maintenance.<br><br>You are a fluke of the universe ...<br>You have no right to be here.<br>Whether you can hear it or not, the universe<br>Is laughing behind your back.<br>&nbsp;-- National Lampoon');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'To err is human, to moo bovine.');
