INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '&nbsp;"What''s that thing?"<br>"Well, it''s a highly technical, sensitive instrument we use in&nbsp;computer repair.  Being a layman, you probably can''t grasp exactly what&nbsp;it does.  We call it a two-by-four."<br>&nbsp;-- Jeff MacNelley, "Shoe"');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Some performers on television appear to be horrible people, but when&nbsp;you finally get to know them in person, they turn out to be even&nbsp;worse.<br>&nbsp;-- Avery');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '&nbsp;THE LESSER-KNOWN PROGRAMMING LANGUAGES #5: VALGOL&nbsp;From its modest beginnings in Southern California''s San Fernando Valley,<br>VALGOL is enjoying a dramatic surge of popularity across the industry.<br>&nbsp;Here is a sample program:<br>LIKE, Y*KNOW(I MEAN)START<br>IF PIZZA = LIKE BITCHEN AND GUY = LIKE TUBULAR AND<br>   VALLEY GIRL = LIKE GRODY**MAX(FERSURE)**2 THEN<br>&nbsp;FOR I = LIKE 1 TO OH*MAYBE 100<br>&nbsp;&nbsp;DO*WAH - (DITTY**2)<br>&nbsp;&nbsp;BARF(I)=TOTALLY GROSS(OUT)<br>&nbsp;SURE<br>LIKE BAG THIS PROGRAM<br>REALLY<br>LIKE TOTALLY (Y*KNOW)<br>IM*SURE<br>GOTO THE MALL&nbsp;&nbsp;When the user makes a syntax error, the interpreter displays the message:<br><br>GAG ME WITH A SPOON!!');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '"You should, without hesitation, pound your typewriter into a&nbsp;plowshare, your paper into fertilizer, and enter agriculture"<br>&nbsp;-- Business Professor, University of Georgia');
