INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'New York is real.  The rest is done with mirrors.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'May your Tongue stick to the Roof of your Mouth with the Force of a&nbsp;Thousand Caramels.');
