INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Q:  Why do ducks have flat feet?&nbsp;A:  To stamp out forest fires.<br>&nbsp;Q:  Why do elephants have flat feet?&nbsp;A:  To stamp out flaming ducks.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Technological progress has merely provided us with more efficient means&nbsp;for going backwards.<br>&nbsp;-- Aldous Huxley');
