INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'I''m very good at integral and differential calculus,<br>I know the scientific names of beings animalculous:<br>In short, in matters vegetable, animal, and mineral,<br>I am the very model of a modern Major-General.<br>&nbsp;-- Gilbert & Sullivan, "Pirates of Penzance"');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'A new dramatist of the absurd&nbsp;Has a voice that will shortly be heard.<br>I learn from my spies<br>He''s about to devise&nbsp;An unprintable three-letter word.');
