INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '"What is wanted is not the will to believe, but the will to find out,<br>which is the exact opposite."<br>&nbsp;-- Bertrand Russell, "Skeptical_Essays", 1928');
