INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'TAURUS (Apr 20 - May 20)<br>You are practical and persistent.  You have a dogged<br>determination and work like hell.  Most people think you are<br>stubborn and bull headed.  You are a Communist.');
