INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Decision maker, n.:<br>The person in your office who was unable to form a task force&nbsp;before the music stopped.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'BULLWINKLE: "You just leave that to my pal.  He''s the brains of the<br>    outfit."&nbsp;GENERAL:    "What does that make YOU?"&nbsp;BULLWINKLE: "What else?  An executive..."<br>&nbsp;-- Jay Ward');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'If you put garbage in a computer nothing comes out but garbage.  But&nbsp;this garbage, having passed through a very expensive machine, is&nbsp;somehow enobled and none dare criticize it.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Did you know that the voice tapes easily identify the Russian pilot&nbsp;that shot down the Korean jet?  At one point he definitely states:<br><br>"Natasha!  First we shoot jet, then we go after moose and<br>squirrel."&nbsp;<br>&nbsp;-- ihuxw!tommyo');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'A citizen of America will cross the ocean to fight for democracy, but&nbsp;won''t cross the street to vote in a national election.<br>&nbsp;-- Bill Vaughan');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Uncle Ed''s Rule of Thumb:<br>Never use your thumb for a rule.  You''ll either hit it with a&nbsp;hammmer or get a splinter in it.');
