INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Magnet, n.: Something acted upon by magnetism&nbsp;&nbsp;Magnetism, n.: Something acting upon a magnet.<br>&nbsp;The two definition immediately foregoing are condensed from the works&nbsp;of one thousand eminent scientists, who have illuminated the subject&nbsp;with a great white light, to the inexpressible advancement of human&nbsp;knowledge.<br>&nbsp;-- Ambrose Bierce, "The Devil''s Dictionary"');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '"For an adequate time call 555-3321"');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '"Love is a snowmobile racing across the tundra and then suddenly it&nbsp;flips over, pinning you underneath.  At night, the ice weasels come."<br>&nbsp;-- Matt Groening');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'America was discovered by Amerigo Vespucci and was named after him,<br>until people got tired of living in a place called "Vespuccia" and&nbsp;changed its name to "America".<br>&nbsp;-- Mike Harding, "The Armchair Anarchist''s Almanac"');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'You know you''ve been spending too much time on the computer when your&nbsp;friend misdates a check, and you suggest adding a "++" to fix it.');
