INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '100 buckets of bits on the bus&nbsp;&nbsp;100 buckets of bits&nbsp;Take one down, short it to ground&nbsp;FF buckets of bits on the bus&nbsp;&nbsp;&nbsp;FF buckets of bits on the bus&nbsp;&nbsp;FF buckets of bits&nbsp;Take one down, short it to ground&nbsp;FE buckets of bits on the bus&nbsp;&nbsp;&nbsp;ad infinitum...');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '"Reintegration complete," ZORAC advised.  "We''re back in the universe&nbsp;again ..."  An unusually long pause followed, "... but I don''t know&nbsp;which part.  We seem to have changed our position in space."  A&nbsp;spherical display in the middle of the floor illuminated to show the&nbsp;starfield surrounding the ship.<br>&nbsp;"Several large, artificial constructions are approaching us," ZORAC&nbsp;announced after a short pause.  "The designs are not familiar, but they&nbsp;are obviously the products of intelligence.  Implications: we have been&nbsp;intercepted deliberately by a means unknown, for a purpose unknown, and&nbsp;transferred to a place unknown by a form of intelligence unknown.<br>Apart from the unknowns, everything is obvious."<br>&nbsp;-- James P. Hogan, "Giants Star"');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'It is the business of the future to be dangerous.<br>&nbsp;-- Hawkwind');
