INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'My pen is at the bottom of a page,<br>Which, being finished, here the story ends:<br>''Tis to be wished it had been sooner done,<br>But stories somehow lengthen when begun.<br>&nbsp;-- Byron');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Fortune''s Law of the Week (this week, from Kentucky):<br>No female shall appear in a bathing suit at any airport in this&nbsp;State unless she is escorted by two officers or unless she is armed&nbsp;with a club.  The provisions of this statute shall not apply to females&nbsp;weighing less than 90 pounds nor exceeding 200 pounds, nor shall it&nbsp;apply to female horses.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'C, n.:<br>A programming language that is sort of like Pascal except more&nbsp;like assembly except that it isn''t very much like either one, or&nbsp;anything else.  It is either the best language available to the art&nbsp;today, or it isn''t.<br>&nbsp;-- Ray Simard');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Hlade''s Law:<br>If you have a difficult task, give it to a lazy person -- they&nbsp;will find an easier way to do it.');
