INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Chicago Transit Authority Rider''s Rule #84:<br>The CTA has complimentary pop-up timers available on request&nbsp;for overheated passengers.  When your timer pops up, the driver will&nbsp;cheerfully baste you.<br>&nbsp;-- Chicago Reader 5/28/82');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'The Advertising Agency Song:<br> <br>When your client''s hopping mad,<br>Put his picture in the ad.<br>If he still should prove refractory,<br>Add a picture of his factory.');
