INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '7:30, Channel 5: The Bionic Dog (Action/Adventure)<br>The Bionic Dog gets a hormonal short-circuit and violates the<br>Mann Act with an interstate Greyhound bus.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '"Acting is an art which consists of keeping the audience from&nbsp;coughing."');
