INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Real programmers disdain structured programming.  Structured&nbsp;programming is for compulsive neurotics who were prematurely toilet-&nbsp;trained.  They wear neckties and carefully line up pencils on otherwise&nbsp;clear desks.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Friends, Romans, Hipsters,<br>Let me clue you in:<br>I come to put down Caesar, not to groove him.<br>The square kicks some cats are on stay with them:<br>The hip bits, like, go down under; so let it lay with Caesar.  The cool Brutus&nbsp;Gave you the message: Caesar had big eyes:<br>If that''s the sound, someone''s copping a plea,<br>And, like, old Caesar really set them straight.<br>Here, copacetic with Brutus and the studs, -- for Brutus is a real cool cat:<br>So are they all, all cool cats, --&nbsp;Come I to make this gig at Caesar''s laying down.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Who''s on first?');
