INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'The hardest thing in the world to understand is the income tax.<br>&nbsp;-- Albert Einstein');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'There''s no trick to being a humorist when you have the whole government&nbsp;working for you.<br>&nbsp;-- Will Rodgers');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '... A solemn, unsmiling, sanctimonious old iceberg who looked like he&nbsp;was waiting for a vacancy in the Trinity.<br>&nbsp;-- Mark Twain');
