INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Bureaucrat, n.:<br>A politician who has tenure.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'There were in this country two very large monopolies.  The larger of&nbsp;the two had the following record: the Vietnam War, Watergate, double-&nbsp;digit inflation, fuel and energy shortages, bankrupt airlines, and the&nbsp;8-cent postcard.  The second was responsible for such things as the&nbsp;transistor, the solar cell, lasers, synthetic crystals, high fidelity&nbsp;stereo recording, sound motion pictures, radio astronomy, negative&nbsp;feedback, magnetic tape, magnetic "bubbles", electronic switching&nbsp;systems, microwave radio and TV relay systems, information theory, the&nbsp;first electrical digital computer, and the first communications&nbsp;satellite.  Guess which one got to tell the other how to run the&nbsp;telephone business?');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Q:  How many IBM CPU''s does it take to execute a job?&nbsp;A:  Four; three to hold it down, and one to rip its head off.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'If only I could be respected without having to be respectable.');
