INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Gold, n.:<br>A soft malleable metal relatively scarce in distribution.  It&nbsp;is mined deep in the earth by poor men who then give it to rich men who&nbsp;immediately bury it back in the earth in great prisons, although gold&nbsp;hasn''t done anything to them.<br>&nbsp;-- Mike Harding, "The Armchair Anarchist''s Almanac"');
