INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Decisions of the judges will be final unless shouted down by a really&nbsp;overwhelming majority of the crowd present.  Abusive and obscene&nbsp;language may not be used by contestants when addressing members of the&nbsp;judging panel, or, conversely, by members of the judging panel when&nbsp;addressing contestants (unless struck by a boomerang).<br>&nbsp;-- Mudgeeraba Creek Emu-Riding and Boomerang-Throwing<br>&nbsp;   Assoc.');
