INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '"I''ll rob that rich person and give it to some poor deserving slob.<br>That will *prove* I''m Robin Hood."<br>&nbsp;-- Daffy Duck, "Robin Hood Daffy", [1958, Chuck Jones]');
