INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Maier''s Law:<br>If the facts don''t conform to the theory, they must be disposed<br>of.<br>&nbsp;Corollaries:<br>(1) The bigger the theory, the better.<br>(2) The experiment may be considered a success if no more than<br>    50% of the observed measurements must be discarded to<br>    obtain a correspondence with the theory.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Easiest Color to Solve on a Rubik''s Cube:<br>Black.  Simply remove all the little colored stickers on the&nbsp;cube, and each of side of the cube will now be the original color of&nbsp;the plastic underneath -- black.  According to the instructions, this&nbsp;means the puzzle is solved.<br>&nbsp;-- Steve Rubenstein');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Beware of low-flying butterflies.');
