INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '"Now the Lord God planted a garden East of Whittier in a place called&nbsp;Yorba Linda, and out of the ground he made to grow orange trees that&nbsp;were good for food and the fruits thereof he labeled SUNKIST ..."<br>&nbsp;-- "The Begatting of a President"');
