INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'James Joyce -- an essentially private man who wished his total&nbsp;indifference to public notice to be universally recognized.<br>&nbsp;-- Tom Stoppard');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Eleanor Rigby<br>Sits at the keyboard<br>And waits for a line on the screen&nbsp;Lives in a dream&nbsp;Waits for a signal<br>Finding some code<br>That will make the machine do some more.<br>What is it for?&nbsp;&nbsp;All the lonely users, where do they all come from?&nbsp;All the lonely users, why does it take so long?');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'If the odds are a million to one against something occurring, chances&nbsp;are 50-50 it will.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '"They that can give up essential liberty to obtain a little temporary&nbsp;safety deserve neither liberty nor safety."<br>&nbsp;-- Benjamin Franklin, 1759');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Committee Rules:<br>(1) Never arrive on time, or you will be stamped a beginner.<br>(2) Don''t say anything until the meeting is half over; this<br>    stamps you as being wise.<br>(3) Be as vague as possible; this prevents irritating the<br>    others.<br>(4) When in doubt, suggest that a subcommittee be appointed.<br>(5) Be the first to move for adjournment; this will make you<br>    popular -- it''s what everyone is waiting for.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'According to Kentucky state law, every person must take a bath at least&nbsp;once a year.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '"We have reason to believe that man first walked upright to free his&nbsp;hands for masturbation."<br>&nbsp;-- Lily Tomlin');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '&nbsp;&nbsp;A Severe Strain on the Credulity&nbsp;&nbsp;As a method of sending a missile to the higher, and even to the highest&nbsp;parts of the earth''s atmospheric envelope, Professor Goddard''s rocket&nbsp;is a practicable and therefore promising device.  It is when one&nbsp;considers the multiple-charge rocket as a traveler to the moon that one&nbsp;begins to doubt ... for after the rocket quits our air and really&nbsp;starts on its journey, its flight would be neither accelerated nor&nbsp;maintained by the explosion of the charges it then might have left.<br>Professor Goddard, with his "chair" in Clark College and countenancing&nbsp;of the Smithsonian Institution, does not know the relation of action to&nbsp;re-action, and of the need to have something better than a vacuum&nbsp;against which to react ... Of course he only seems to lack the&nbsp;knowledge ladled out daily in high schools.<br>&nbsp;-- New York Times Editorial, 1920');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Never worry about theory as long as the machinery does what it''s&nbsp;supposed to do.<br>&nbsp;-- R. A. Heinlein');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Justice, n.:<br>A decision in your favor.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Bank error in your favor.  Collect $200.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Here is a simple experiment that will teach you an important electrical&nbsp;lesson: On a cool, dry day, scuff your feet along a carpet, then reach&nbsp;your hand into a friend''s mouth and touch one of his dental fillings.<br>Did you notice how your friend twitched violently and cried out in&nbsp;pain?  This teaches us that electricity can be a very powerful force,<br>but we must never use it to hurt others unless we need to learn an&nbsp;important electrical lesson.<br>&nbsp;It also teaches us how an electrical circuit works.  When you scuffed&nbsp;your feet, you picked up batches of "electrons", which are very small&nbsp;objects that carpet manufacturers weave into carpets so they will&nbsp;attract dirt.  The electrons travel through your bloodstream and&nbsp;collect in your finger, where they form a spark that leaps to your&nbsp;friend''s filling, then travels down to his feet and back into the&nbsp;carpet, thus completing the circuit.<br>&nbsp;Amazing Electronic Fact: If you scuffed your feet long enough without&nbsp;touching anything, you would build up so many electrons that your&nbsp;finger would explode!  But this is nothing to worry about unless you&nbsp;have carpeting.<br>&nbsp;-- Dave Barry, "What is Electricity?"');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Money is better than poverty, if only for financial reasons.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'If you don''t have a nasty obituary you probably didn''t matter.<br>&nbsp;-- Freeman Dyson');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '&nbsp;Before he became a hermit, Zarathud was a young Priest, and&nbsp;took great delight in making fools of his opponents in front of his&nbsp;followers.<br>One day Zarathud took his students to a pleasant pasture and&nbsp;there he confronted The Sacred Chao while She was contentedly grazing.<br>"Tell me, you dumb beast," demanded the Priest in his&nbsp;commanding voice, "why don''t you do something worthwhile?  What is your&nbsp;Purpose in Life, anyway?"<br>Munching the tasty grass, The Sacred Chao replied "MU".  (The&nbsp;Chinese ideogram for NO-THING.)<br>Upon hearing this, absolutely nobody was enlightened.<br>Primarily because nobody understood Chinese.<br>&nbsp;-- Camden Benares, "Zen Without Zen Masters"');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Fifth Law of Procrastination:<br>Procrastination avoids boredom; one never has the feeling that&nbsp;there is nothing important to do.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Flappity, floppity, flip&nbsp;The mouse on the m"^Hobius strip;<br>The strip revolved,<br>The mouse dissolved&nbsp;In a chronodimensional skip.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '"You can write a small letter to Grandma in the filename."<br>&nbsp;-- Forbes Burkowski, Computer Science 454');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'It is only the great men who are truly obscene.  If they had not dared&nbsp;to be obscene, they could never have dared to be great.<br>&nbsp;-- Havelock Ellis');
