INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'I gave up Smoking, Drinking and Sex.  It was the most *__________^H^H^H^H^H^H^H^H^H^Hhorrifying* 20&nbsp;minutes of my life!');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'I''m changing my name to Chrysler&nbsp;I''m going down to Washington, D.C.<br>I''ll tell some power broker<br>What they did for Iacocca&nbsp;Will be perfectly acceptable to me!&nbsp;I''m changing my name to Chrysler,<br>I''m heading for that great receiving line.<br>When they hand a million grand out,<br>I''ll be standing with my hand out,<br>Yessir, I''ll get mine!<br>&nbsp;-- Tom Paxton');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Telephone, n.:<br>An invention of the devil which abrogates some of the&nbsp;advantages of making a disagreeable person keep his distance.<br>&nbsp;-- Ambrose Bierce');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Some don''t prefer the pursuit of happiness to the happiness of pursuit.');
