INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '"The good Christian should beware of mathematicians and all those who&nbsp;make empty prophecies.  The danger already exists that mathematicians&nbsp;have made a covenant with the devil to darken the spirit and confine&nbsp;man in the bonds of Hell."<br>&nbsp;-- St. Augustine');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Men were real men, women were real women, and small, furry creatures&nbsp;from Alpha Centauri were REAL small, furry creatures from Alpha&nbsp;Centauri.  Spirits were brave, men boldly split infinitives that no man&nbsp;had split before.  Thus was the Empire forged.<br>&nbsp;-- "The Hitchhiker''s Guide to the Galaxy", Douglas Adams');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Canada Bill Jone''s Motto:<br>It''s morally wrong to allow suckers to keep their money.<br>&nbsp;Supplement:<br>A .44 magnum beats four aces.');
