INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '&nbsp;During a grouse hunt in North Carolina two intrepid sportsmen&nbsp;were blasting away at a clump of trees near a stone wall.  Suddenly a&nbsp;red-faced country squire popped his head over the wall and shouted,<br>"Hey, you almost hit my wife."<br>"Did I?"  cried the hunter, aghast.  "Terribly sorry.  Have a&nbsp;shot at mine, over there."');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '&nbsp;Hug O'' War&nbsp;&nbsp;I will not play at tug o'' war.<br>I''d rather play at hug o'' war,<br>Where everyone hugs&nbsp;Instead of tugs,<br>Where everyone giggles&nbsp;And rolls on the rug,<br>Where everyone kisses,<br>And everyone grins,<br>And everyone cuddles,<br>And everyone wins.<br>&nbsp;-- Shel Silverstein');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '"Deep" is a word like "theory" or "semantic" -- it implies all sorts of&nbsp;marvelous things.  It''s one thing to be able to say "I''ve got a&nbsp;theory", quite another to say "I''ve got a semantic theory", but, ah,<br>those who can claim "I''ve got a deep semantic theory", they are truly&nbsp;blessed.<br>&nbsp;-- Randy Davis');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Do not read this fortune under penalty of law.<br>Violators will be prosecuted.<br>(Penal Code sec. 2.3.2 (II.a.))');
