INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Politician, n.:<br>From the Greek "poly" ("many") and the French "tete" ("head" or&nbsp;"face," as in "tete-a-tete": head to head or face to face).  Hence&nbsp;"polytetien", a person of two or more faces.<br>&nbsp;-- Martin Pitt');
