INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Mathematicians often resort to something called Hilbert space, which is&nbsp;described as being n-dimensional.  Like modern sex, any number can&nbsp;play.<br>&nbsp;-- Dr. Thor Wald, in "Beep/The Quincunx of Time", by<br>&nbsp;   James Blish');
