INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Besides the device, the box should contain:<br>&nbsp;* Eight little rectangular snippets of paper that say "WARNING"&nbsp;&nbsp;* A plastic packet containing four 5/17 inch pilfer grommets and two&nbsp;  club-ended 6/93 inch boxcar prawns.<br>&nbsp;YOU WILL NEED TO SUPPLY: a matrix wrench and 60,000 feet of tram&nbsp;cable.<br>&nbsp;IF ANYTHING IS DAMAGED OR MISSING: You IMMEDIATELY should turn to your&nbsp;spouse and say: "Margaret, you know why this country can''t make a car&nbsp;that can get all the way through the drive-through at Burger King&nbsp;without a major transmission overhaul?  Because nobody cares, that''s&nbsp;why."&nbsp;&nbsp;WARNING: This is assuming your spouse''s name is Margaret.<br>&nbsp;-- Dave Barry, "Read This First!"');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '"The last time somebody said, `I find I can write much better with a&nbsp;word processor.'', I replied, `They used to say the same thing about&nbsp;drugs.''<br>&nbsp;-- Roy Blount, Jr.');
