INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '"Wouldn''t the sentence ''I want to put a hyphen between the words Fish&nbsp;and And and And and Chips in my Fish-And-Chips sign'' have been clearer&nbsp;if quotation marks had been placed before Fish, and between Fish and&nbsp;and, and and and And, and And and and, and and and And, and And and&nbsp;and, and and and Chips, as well as after Chips?"');
