INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'The primary theme of SoupCon is communication.  The acronym "LEO"&nbsp;represents the secondary theme:<br><br>Law Enforcement Officials&nbsp;&nbsp;The overall theme of SoupCon shall be:<br><br>Avoiding Communication with Law Enforcement Officials');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Good advice is something a man gives when he is too old to set a bad&nbsp;example.<br>&nbsp;-- La Rouchefoucauld');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '&nbsp;A musician of more ambition than talent composed an elegy at&nbsp;the death of composer Edward MacDowell.  She played the elegy for the&nbsp;pianist Josef Hoffman, then asked his opinion.  "Well, it''s quite&nbsp;nice," he replied, but don''t you think it would be better if ..."<br>"If what?"  asked the composer.<br>"If ... if you had died and MacDowell had written the elegy?"');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '"If God lived on Earth, people would knock out all His windows."<br>&nbsp;-- Yiddish saying');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'A very intelligent turtle&nbsp;Found programming UNIX a hurdle<br>The system, you see,<br>Ran as slow as did he,<br>And that''s not saying much for the turtle.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'There is nothing wrong with Southern California that a rise in the&nbsp;ocean level wouldn''t cure.<br>&nbsp;-- Ross MacDonald');
