INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '&nbsp;THE LESSER-KNOWN PROGRAMMING LANGUAGES #17: SARTRE&nbsp;&nbsp;Named after the late existential philosopher, SARTRE is an extremely&nbsp;unstructured language.  Statements in SARTRE have no purpose; they just&nbsp;are.  Thus SARTRE programs are left to define their own functions.<br>SARTRE programmers tend to be boring and depressed, and are no fun at&nbsp;parties.');
