INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'All [zoos] actually offer to the public in return for the taxes spent&nbsp;upon them is a form of idle and witless amusement, compared to which a&nbsp;visit to a penitentiary, or even to a State legislature in session, is&nbsp;informing, stimulating and ennobling.<br>&nbsp;-- H. L. Mencken');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Barometer, n.:<br>An ingenious instrument which indicates what kind of weather we&nbsp;are having.<br>&nbsp;-- Ambrose Bierce, "The Devil''s Dictionary"');
