INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'There are two kinds of solar-heat systems: "passive" systems collect&nbsp;the sunlight that hits your home, and "active" systems collect the&nbsp;sunlight that hits your neighbors'' homes, too.<br>&nbsp;-- Dave Barry, "Postpetroleum Guzzler"');
