INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Accidents cause History.<br>&nbsp;If Sigismund Unbuckle had taken a walk in 1426 and met Wat Tyler, the&nbsp;Peasant''s Revolt would never have happened and the motor car would not&nbsp;have been invented until 2026, which would have meant that all the oil&nbsp;could have been used for lamps, thus saving the electric light bulb and&nbsp;the whale, and nobody would have caught Moby Dick or Billy Budd.<br>&nbsp;-- Mike Harding, "The Armchair Anarchist''s Almanac"');
