INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'If I have seen farther than others, it is because I was standing on the&nbsp;shoulders of giants.<br>&nbsp;-- Isaac Newton&nbsp;&nbsp;In the sciences, we are now uniquely privileged to sit side by side&nbsp;with the giants on whose shoulders we stand.<br>&nbsp;-- Gerald Holton&nbsp;&nbsp;If I have not seen as far as others, it is because giants were standing&nbsp;on my shoulders.<br>&nbsp;-- Hal Abelson&nbsp;&nbsp;In computer science, we stand on each other''s feet.<br>&nbsp;-- Brian K. Reid');
