INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'On account of being a democracy and run by the people, we are the only&nbsp;nation in the world that has to keep a government four years, no matter&nbsp;what it does.<br>&nbsp;-- Will Rogers');
